    [0xE0, 0x9F, 0x80]
  ]

  lemma TestDecodeIllFormedSequence()
    ensures forall s | s in TEST_ILL_FORMED_SEQUENCES
              :: DecodeCodeUnitSequenceChecked(s).None?
  {}

}

