module {:options "-functionSyntax:4"} Wrappers {

  datatype Option<+T> = None | Some(value: T) {
    function ToResult(): Result<T, string> {
      match this
      case Some(v) => Success(v)
      case None() => Failure("Option is None")
    }

    function ToResult'<R>(error: R): Result<T, R> {
      match this
      case Some(v) => Success(v)
      case None() => Failure(error)
    }

    function UnwrapOr(default: T): T {
      match this
      case Some(v) => v
      case None() => default
    }

    predicate IsFailure() {
      None?
    }

    function PropagateFailure<U>(): Option<U>
      requires None?
    {
      None
    }

    function Extract(): T
      requires Some?
    {
      value
    }
  }

  datatype Result<+T, +R> = | Success(value: T) | Failure(error: R) {
    function ToOption(): Option<T>
    {
      match this
      case Success(s) => Some(s)
      case Failure(e) => None()
    }

    function UnwrapOr(default: T): T
    {
      match this
      case Success(s) => s
      case Failure(e) => default
    }

    predicate IsFailure() {
      Failure?
    }

    function PropagateFailure<U>(): Result<U, R>
      requires Failure?
    {
      Failure(this.error)
    }

    function MapFailure<NewR>(reWrap: R -> NewR): Result<T, NewR>
    {
      match this
      case Success(s) => Success(s)
      case Failure(e) => Failure(reWrap(e))
    }

    function Extract(): T
      requires Success?
    {
      value
    }
  }

  datatype Outcome<E> = Pass | Fail(error: E)
  {
    predicate IsFailure() {
      Fail?
    }
    // Note: PropagateFailure returns a Result, not an Outcome.
    function PropagateFailure<U>(): Result<U, E>
      requires Fail?
    {
      Failure(this.error)
    }
    // Note: no Extract method
  }

  // A helper function to ensure a requirement is true at runtime
  // :- Need(5 == |mySet|, "The set MUST have 5 elements.")
  function Need<E>(condition: bool, error: E): (result: Outcome<E>)
  {
    if condition then Pass else Fail(error)
  }
}

function Fib(n: nat): nat
  decreases n
{
  if n < 2 then n else Fib(n - 2) + Fib(n - 1)
}

function SeqSum(s: seq<int>, lo: int, hi: int): int
  requires 0 <= lo <= hi <= |s|
  decreases hi - lo
{
  if lo == hi then 0 else s[lo] + SeqSum(s, lo + 1, hi)
}

function Ack(m: nat, n: nat): nat
  // decreases ...?
{
  if m == 0 then
    n + 1
  else if n == 0 then
    Ack(m - 1, 1)
  else
    Ack(m - 1, Ack(m, n - 1))
}

// Exercises:

function F(x: int): int
  // decreases ...?
{
  if x < 10 then x else F(x - 1)
}

function G(x: int): int
  // decreases ...?
{
  if 0 <= x then G(x - 2) else x
}

function H(x: int): int
  // decreases ...?
{
  if x < -60 then x else H(x - 1)
}

function I(x: nat, y: nat): int
  // decreases ...?
{
  if x == 0 || y == 0 then
    12
  else if x % 2 == y % 2 then
    I(x - 1, y)
  else
    I(x, y - 1)
}

function J(x: nat, y: nat): int
  // decreases ...?
{
  if x == 0 then
    y
  else if y == 0 then
    J(x - 1, 3)
  else
    J(x, y - 1)
}

function K(x: nat, y: nat, z: nat): int
  // decreases ...?
{
  if x < 10 || y < 5 then
    x + y
  else if z == 0 then
    K(x - 1, y, 5)
  else
    K(x, y - 1, z - 1)
}

function L(x: int): int
  // decreases ...?
{
  if x < 100 then L(x + 1) + 10 else x
}

function M(x: int, b: bool): int
  // decreases ...?
{
  if b then x else M(x + 25, true)
}

function N(x: int, y: int, b: bool): int
  // decreases ...?
{
  if x <= 0 || y <= 0 then
    x + y
  else if b then
    N(x, y + 3, !b)
  else
    N(x - 1, y, true)
}

// Simple recursion

function ExpLess1S(n: nat): nat {
  if n == 0 then 0 else 2 * ExpLess1S(n - 1) + 1
}

// Mutual recursion

function ExpLess1(n: nat): nat
  decreases n, 1
{
  if n == 0 then 0 else ExpLess2(n) + 1
}

function ExpLess2(n: nat): nat
  requires 1 <= n
  decreases n, 0
{
  2 * ExpLess1(n - 1)
}

method BadDouble(x: int) returns (d: int)
  ensures d == 2 * x
{
  var y := BadDouble(x - 1); // error: failure to prove termination
  d := y + 2;
}

method PartialId(x: int) returns (y: int)
  ensures y == x
{
  if x % 2 == 0 {
    y := x;
  } else {
    y := PartialId(x); // error: failure to prove termination
  }
}

method Squarish(x: int, guess: int) returns (y: int)
  ensures x * x == y
{
  if
  case guess == x * x => // good guess!
    y := guess;
  case true =>
    y := Squarish(x, guess - 1); // error: failure to prove termination
  case true =>
    y := Squarish(x, guess + 1); // error: failure to prove termination
}

method Impossible(x: int) returns (y: int)
  ensures y % 2 == 0 && y == 10 * x - 3
{
  y := Impossible(x); // error: failure to prove termination
}

function Dubious(): int {
  1 + Dubious() // error: failure to prove termination
}

function G(n: nat): nat
  // decreases ...?
{
  if n == 0 then 0 else n - G(G(n - 1))
}

function F(n: nat): nat
  // decreases ...?
{
  if n == 0 then 1 else n - M(F(n - 1))
}

function M(n: nat): nat
  // decreases ...?
{
  if n == 0 then 0 else n - F(M(n - 1))
}

method RequiredStudyTime(c: nat) returns (hours: nat)
  ensures hours <= 200

method Study(n: nat, h: nat)
  decreases n, h
{
  if h != 0 {
    // first, study for an hour, and then:
    Study(n, h - 1);
  } else if n == 0 {
    // you just finished course 0 -- woot woot, graduation time!
  } else {
    // find out how much studying is needed for the next course
    var hours := RequiredStudyTime(n - 1);
    // get started with course n-1:
    Study(n - 1, hours);
  }
}

method StudyPlan(n: nat)
  requires n <= 40
  decreases 40 - n
{
  if n == 40 {
    // done
  } else {
    var hours := RequiredStudyTime(n);
    Learn(n, hours);
  }
}

method Learn(n: nat, h: nat)
  requires n < 40
  decreases 40 - n, h
{
  if h == 0 {
    // done with class n; continue with the rest of the study plan
    StudyPlan(n + 1);
  } else {
    // some learning to take place here...
    Learn(n, h - 1);
  }
}

