method InitMatrix<T>(a: array2<T>, d: T)
  modifies a
  ensures forall i, j ::
    0 <= i < a.Length0 && 0 <= j < a.Length1 ==> a[i,j] == d
{
  var m := 0;
  while m != a.Length0
    invariant 0 <= m <= a.Length0
    invariant forall i, j ::
      0 <= i < m && 0 <= j < a.Length1 ==> a[i,j] == d
  {
    var n := 0;
    while n != a.Length1
      invariant 0 <= n <= a.Length1
      invariant forall i, j ::
        0 <= i < m && 0 <= j < a.Length1 ==> a[i,j] == d
      invariant forall j :: 0 <= j < n ==> a[m,j] == d
    {
      a[m,n] := d;
      n := n + 1;
    }
    m := m + 1;
  }
}

