function DivMod(x: Unary, y: Unary): (Unary, Unary)
  requires y != Zero
  decreases UnaryToNat(x)
{
  if Less(x, y) then
    (Zero, x)
  else
    SubCorrect(x, y);
    var r := DivMod(Sub(x, y), y);
    (Suc(r.0), r.1)
}

