  var a := new int[100];
  assert a.Length == 100;
  a := new int[20];

  a[9] := a[9] + 5;

  a[6] := 2;
  a[7] := 3;
  assert a[6] == 2 && a[7] == 3;
}

method TestArrayElements(j: nat, k: nat)
  requires j < 10 && k < 10
{
  var a := new int[10];
  a[j] := 60;
  a[k] := 65;
  if j == k {
    assert a[j] == 65;
  } else {
