  }
}

method ArraysAreReferences() {
  var a := new string[20];
  a[7] := "hello";

  var b := a;
  assert b[7] == "hello";

  b[7] := "hi";
  a[8] := "greetings";
  assert a[7] == "hi" && b[8] == "greetings";

  b := new string[8];
  b[7] := "long time, no see";
  assert a[7] == "hi";
  assert a.Length == 20 && b.Length == 8;
}

