class TrickyLets
{
  var next: TrickyLets?
  var arr: array<char>

  constructor (n: nat, w: TLWrapper?)
    modifies w
    ensures w != null ==> w.data == null
  {
    if w != null {
      w.data := null;
    }
    if n != 0 {
      next := new TrickyLets(n-1, null);
    }
  }
  method RegressionTests()
  {
    // make sure l-values are captured before any assignment takes place
    var t := new TrickyLets(0, null);
    var w := new TLWrapper;
    w.data := t;
    w.data, w.data.next := null, null;

    // ditto
    t := new TrickyLets(0, null);
    w := new TLWrapper;
    w.data := t;
    w.data.next, w.data := null, null;

    // (here's the thing that once had been buggy)
    // also check that l-values are captured when the RHS is a "new"
    t := new TrickyLets(0, null);
    w := new TLWrapper;
    w.data := t;
    w.data.next := new TrickyLets(0, w);
    assert t.next != null;
    assert w.data == null;
  }
  method Do()
    modifies this
  {
    this.arr := new char[20](_ => 'D');
    assert arr[12] == 'D';
    print arr[12], " ";
    // Now, do the same thing, but with various let expressions
    (var u := this; u).arr := new char[var n := 20; n](var fn := _ => 'E'; fn);
    assert arr[13] == 'E';
    print arr[13], "\n";
  }
  method DoDisplay()
    modifies this
  {
    this.arr := new char[3] ['x', 'y', 'z'];
    assert arr[1] == 'y';
    print arr[1], " ";
    // Now, do the same thing, but with various let expressions
    (var u := this; u).arr := new char[var n := 3; n] [var x := 'x'; x, var y := 'y'; y, var z := 'z'; z];
    assert arr[2] == 'z';
    print arr[2], "\n";
  }
  method DoDefault()
    modifies this
  {
    this.arr := new char[4];
    assert arr.Length == 4;
    print arr.Length, " ";
    // Now, do (almost) the same thing, but with various let expressions
    (var u := this; u).arr := new char[var n := 3; n];
    assert arr.Length == 3;
    print arr.Length, "\n";
  }
}

class TLWrapper
{
  var data: TrickyLets?
}

