  }
}

newtype Primes = x | 2 <= x && forall y :: 2 <= y < x ==> x % y != 0 witness 2
{
  const c: int := this as int * 2
  static const g: int := 18
  function F(x: int): int { 2 * x + this as int }
  static function G(x: int): int { 100 - x }
