method mp(m: map<int,int>)
{
  var items := m.Items;
  while items != {}
    decreases |items|
  {
    var item :| item in items;
    items := items - { item };
    print item.0, " ", item.1, "\n";
  }
}

method mpi(m: imap<int,int>)
{
