method mseq(s: seq<int>) {
  var i: int := 0;
  var sum: int := 0;
  while i < |s|
    decreases |s| - i
  {
    sum := sum + s[i];
    i := i + 1;
  }
}

method marr(s: array<int>) {
  var i: int := 0;
  var sum: int := 0;
  while i < s.Length
    decreases s.Length - i
  {
    sum := sum + s[i];
    i := i + 1;
  }

  var rev := new int[s.Length];
  forall i | 0 <= i < s.Length {
    rev[i] := s[s.Length-i-1];
  }
}

