  TestP(28, 30);
  TestP(255, 255); // this one requires an unsigned comparison in Java
  TestP(0, 255);

  TestQ(-128, -128);
  TestQ(-128, -127);
  TestQ(126, 127);
  TestQ(127, 127);

  Mul(0, 2);
  Mul(2, 0);
  Mul(1, 1);
  Mul(6, 5);

