// Test "break break"
method BC30() returns (c: nat) { // 15
  c := 0;
  while true {
    for k := 0 to 10
      invariant k <= 5
    {
      if k == 5 {
        break break;
      }
      c := c + 1;
    }
  }

  while {
    case true =>
      for k := 0 to 10
        invariant k <= 5
      {
        if k == 5 {
          break break;
        }
        c := c + 1;
      }
  }

  for i := 0 to 100 {
    for k := 0 to 10 {
      if k == 5 {
        break break;
      }
      c := c + 1;
    }
  }
}

method LabelRegressions() {
  // The cases of if-case, while-case, and match statements are List<Statement>'s, which are essentially
  // a BlockStmt but without the curly braces. Each Statement in such a List can have labels, so
  // it's important to call TrStmtList on these in the Verifier, not just call TrStmt on every Statement
  // in the List. (See also LabelRegressions() in Test/dafny0/ResolutionErrors.dfy.)
  if {
    case true =>
      label Loop:
      for k := 0 to 10 {
        if k % 2 == 0 {
          continue Loop;
        } else {
          break Loop;
        }
      }
  }

  while {
    case true =>
      label Loop:
      for k := 0 to 10 {
        if k % 2 == 0 {
          continue Loop;
        } else {
          break Loop;
        }
      }
      break;
  }

  match (0, 0) {
    case (_, _) =>
      label Loop:
      for k := 0 to 10 {
        if k % 2 == 0 {
          continue Loop;
        } else {
          break Loop;
        }
      }
  }

  print "all good\n";
}

