datatype NatOutcome =
| NatSuccess(value: nat)
| NatFailure(error: string)
{
    predicate IsFailure() {
        this.NatFailure?
    }
    function PropagateFailure(): NatOutcome requires IsFailure() {
        this
    }
    function Extract(): nat requires !IsFailure() {
        this.value
    }
}

